const {
  createFruitArray,
  getFirstElement,
  getLastElement,
  addToEnd
} = require('./exercise');

describe('Array Basics Exercise', () => {
  test('createFruitArray should return an array with 3 elements', () => {
    const result = createFruitArray();
    expect(Array.isArray(result)).toBe(true);
    expect(result.length).toBe(3);
  });

  test('createFruitArray should contain only strings', () => {
    const result = createFruitArray();
    result.forEach(item => {
      expect(typeof item).toBe('string');
    });
  });

  test('getFirstElement should return the first element', () => {
    expect(getFirstElement([1, 2, 3])).toBe(1);
    expect(getFirstElement(['a', 'b', 'c'])).toBe('a');
  });

  test('getLastElement should return the last element', () => {
    expect(getLastElement([1, 2, 3])).toBe(3);
    expect(getLastElement(['x', 'y', 'z'])).toBe('z');
  });

  test('getLastElement should work with different array lengths', () => {
    expect(getLastElement([1])).toBe(1);
    expect(getLastElement([1, 2, 3, 4, 5])).toBe(5);
  });

  test('addToEnd should add an item to the end of the array', () => {
    const arr = [1, 2, 3];
    const result = addToEnd(arr, 4);
    expect(result).toEqual([1, 2, 3, 4]);
    expect(arr).toEqual([1, 2, 3, 4]);
  });

  test('addToEnd should return the modified array', () => {
    const arr = ['a', 'b'];
    const result = addToEnd(arr, 'c');
    expect(result).toBe(arr);
  });
});

