function createFruitArray() {
  return ['apple', 'banana', 'orange'];
}

function getFirstElement(arr) {
  return arr[0];
}

function getLastElement(arr) {
  return arr[arr.length - 1];
}

function addToEnd(arr, item) {
  arr.push(item);
  return arr;
}

module.exports = {
  createFruitArray,
  getFirstElement,
  getLastElement,
  addToEnd
};

