const {
  isAdult,
  getGrade,
  canVote,
  maxOfTwo
} = require('./exercise');

describe('Conditional Statements Exercise', () => {
  describe('isAdult', () => {
    test('should return true for age 18 and above', () => {
      expect(isAdult(18)).toBe(true);
      expect(isAdult(25)).toBe(true);
      expect(isAdult(65)).toBe(true);
    });

    test('should return false for age below 18', () => {
      expect(isAdult(17)).toBe(false);
      expect(isAdult(10)).toBe(false);
      expect(isAdult(0)).toBe(false);
    });
  });

  describe('getGrade', () => {
    test('should return "A" for scores 90-100', () => {
      expect(getGrade(90)).toBe('A');
      expect(getGrade(95)).toBe('A');
      expect(getGrade(100)).toBe('A');
    });

    test('should return "B" for scores 80-89', () => {
      expect(getGrade(80)).toBe('B');
      expect(getGrade(85)).toBe('B');
      expect(getGrade(89)).toBe('B');
    });

    test('should return "C" for scores 70-79', () => {
      expect(getGrade(70)).toBe('C');
      expect(getGrade(75)).toBe('C');
    });

    test('should return "D" for scores 60-69', () => {
      expect(getGrade(60)).toBe('D');
      expect(getGrade(65)).toBe('D');
    });

    test('should return "F" for scores below 60', () => {
      expect(getGrade(59)).toBe('F');
      expect(getGrade(30)).toBe('F');
      expect(getGrade(0)).toBe('F');
    });
  });

  describe('canVote', () => {
    test('should return true when 18+ and citizen', () => {
      expect(canVote(18, true)).toBe(true);
      expect(canVote(25, true)).toBe(true);
    });

    test('should return false when under 18', () => {
      expect(canVote(17, true)).toBe(false);
      expect(canVote(16, false)).toBe(false);
    });

    test('should return false when not a citizen', () => {
      expect(canVote(25, false)).toBe(false);
      expect(canVote(18, false)).toBe(false);
    });
  });

  describe('maxOfTwo', () => {
    test('should return the larger number', () => {
      expect(maxOfTwo(10, 5)).toBe(10);
      expect(maxOfTwo(3, 8)).toBe(8);
    });

    test('should handle negative numbers', () => {
      expect(maxOfTwo(-5, -10)).toBe(-5);
      expect(maxOfTwo(-3, 2)).toBe(2);
    });

    test('should handle equal numbers', () => {
      const result = maxOfTwo(5, 5);
      expect(result).toBe(5);
    });
  });
});

