// Solution for Conditional Statements exercise

function isAdult(age) {
  return age >= 18;
}

function getGrade(score) {
  if (score >= 90) {
    return 'A';
  } else if (score >= 80) {
    return 'B';
  } else if (score >= 70) {
    return 'C';
  } else if (score >= 60) {
    return 'D';
  } else {
    return 'F';
  }
}

function canVote(age, isCitizen) {
  return age >= 18 && isCitizen;
}

function maxOfTwo(a, b) {
  return a >= b ? a : b;
  // Alternative: if (a >= b) { return a; } else { return b; }
}

module.exports = {
  isAdult,
  getGrade,
  canVote,
  maxOfTwo
};
